(104, 'TEMPRANILLO VALDEPENAS D.O. MARQUÉS DE CAMPOBLANCO GRAND RESERVA', 'S4054', '', 'TEMPRANILLO-VALDEPENAS-D-O-MARQUES-DE-CAMPOBLANCO-GRAND-RESERVA', 'TEMPRANILLO VALDEPENAS D.O. MARQUÉS DE CAMPOBLANCO GRAND RESERVA', '<p>Trocken, fruchtig, kräftig mit feinen Tanninen.</p>', '', 0, 7.5, 0, '', 0, 'S4054.jpg', 0, 0, 0, 0, 1, 1, '2012-10-04 11:24:57', 0),
(105, 'TEMPRANILLO D.O. CASTILLO DE LA CRUZ GRAN RESERVA', 'S4055', '', 'TEMPRANILLO-D-O-CASTILLO-DE-LA-CRUZ-GRAN-RESERVA', 'TEMPRANILLO D.O. CASTILLO DE LA CRUZ GRAN RESERVA', '<p>Reif und tanninhaltig, Aroma nach Kirschen und Erbeeren.</p>', '', 0, 7.5, 0, '', 0, 'S4055.jpg', 0, 0, 0, 0, 1, 1, '2012-10-04 11:26:45', 0),
(106, 'TEMPRANILLO D.O. FINCA LA CRUZ', 'S4056', '', 'TEMPRANILLO-D-O-FINCA-LA-CRUZ', 'TEMPRANILLO D.O. FINCA LA CRUZ', '<p>Feine Holznote, Aromen von schwarzen Früchten, weicher eleganter  Geschmack.</p>', '', 0, 7.5, 0, '', 0, 'S4056.jpg', 0, 0, 0, 0, 1, 1, '2012-10-04 11:29:54', 0),
(107, 'TEMPRANILLO D.O. DON DANIEL GRAN RESERVA', 'S4057', '', 'TEMPRANILLO-D-O-DON-DANIEL-GRAN-RESERVA', 'TEMPRANILLO D.O. DON DANIEL GRAN RESERVA', '<p>Feine Tanninstruktur, kraftvolle Aromen von Waldfrüchten, Gewürzen und  Vanille.</p>', '', 0, 7.5, 0, '', 0, 'S4057.jpg', 0, 0, 0, 0, 1, 1, '2012-10-04 11:31:42', 0),
(108, 'RIOJA D.O.Ca. RESERVA ANTIGUA ULSANZA', 'S4058', '', 'RIOJA-D-O-Ca-RESERVA-ANTIGUA-ULSANZA', 'RIOJA D.O.Ca. RESERVA ANTIGUA ULSANZA', '<p>Kraftvoll, elegant mit Aromen von reifen roten Früchten.</p>', '', 15.5, 10.5, 0, '', 0, 'S4058.jpg', 0, 0, 0, 0, 1, 1, '2012-10-04 11:34:14', 0),
(109, 'RIOJA D.O.Ca. RESERVA FAUSTINO RIVERO ULECIA', 'S4059', '', 'RIOJA-D-O-Ca-RESERVA-FAUSTINO-RIVERO-ULECIA', 'RIOJA D.O.Ca. RESERVA FAUSTINO RIVERO ULECIA', '<p>Trocken, körperreich, komplex, seidig und ausgewogen, langanhaltender  Geschmack.</p>', '', 15.5, 12.5, 0, '', 0, 'S4059.jpg', 0, 0, 0, 0, 1, 1, '2012-10-04 11:37:48', 0),
(110, 'TEMPRANILLO RIOJA CRIANZA EL COTO DOC MAGNUM', 'S4060', '', 'TEMPRANILLO-RIOJA-CRIANZA-EL-COTO-DOC-MAGNUM', 'TEMPRANILLO RIOJA CRIANZA EL COTO DOC MAGNUM', '<p>Lakritznoten, ausgewogen im Mun, seidig und samtig.</p>', '', 23.5, 15.5, 0, '', 0, 'S4060.jpg', 0, 0, 0, 0, 1, 1, '2012-10-04 11:40:18', 0),
(111, 'TINTA DE TORO CRIANZA D.O. CAMPARRON', 'S4061', '', 'TINTA-DE-TORO-CRIANZA-D-O-CAMPARRON', 'TINTA DE TORO CRIANZA D.O. CAMPARRON', '<p>Würzig, leicht rauchig in der Nase, Aroma nach Vanille und dunkle Beeren.</p>', '', 12.5, 10.5, 0, '', 0, 'S4061.jpg', 0, 0, 0, 0, 1, 1, '2012-10-04 11:42:21', 0),
(112, 'RIOJA D.O.Ca. LACRIMUS CRIANZA COSECHA', 'S4062', '', 'RIOJA-D-O-Ca-LACRIMUS-CRIANZA-COSECHA', 'RIOJA D.O.Ca. LACRIMUS CRIANZA COSECHA', '<p>Farbe: kirschrot. Charakter: In der Nase ausdrucksstark und fruchtig, am  Gaumen vollmundig und sehr weich. Sehr saftig mit eleganten, weichen  Tanninen.</p>', '', 15.5, 12.5, 0, '', 0, 'S4062.jpg', 0, 0, 0, 0, 1, 1, '2012-10-04 11:44:02', 0),
(113, 'RIBERA DEL DUERO MIROS CRIANZA', 'S4063', '', 'RIBERA-DEL-DUERO-MIROS-CRIANZA', 'RIBERA DEL DUERO MIROS CRIANZA', '<p>Eine wunderschöne, gleichmässig rote Farbe von Süsskirschen mit mittelhoher  Deckung. In der Nase zeigt er sich elegant, komplex und von guter aromatischer  Intensität. Zunächst machen sich Noten von geröstetem Zedernholz und Gewürzen  (Vanille und Muskatnuss) bemerkbar, danach kommen Erinnerungen an Kakao und  Konfitüre aus roten Früchten (Kirschen) gefolgt von eleganten Anklängen aus der  Balsamreihe (Minze) sowie Aromen von mediterranem Buschwald. Im Mund ist er  schmackhaft, gut strukturiert und mit guten taktilen Eigenschaften, die mit dem  Gesamteindruck verschmelzen, ihm jedoch Breite und Volumen verleihen; gutes  Gleichgewicht zwischen Frucht und Holz. Langer Nachhall geprägt von Anklängen an  Gewürze und Röstung.</p>', '', 24.7, 12.5, 0, '', 0, 'S4063.jpg', 0, 0, 0, 0, 1, 1, '2012-10-04 11:45:29', 0),
(114, 'TEMPRANILLO RESERVA DOC BARON DE LEY', 'S4064', '', 'TEMPRANILLO-RESERVA-DOC-BARON-DE-LEY', 'TEMPRANILLO RESERVA DOC BARON DE LEY', '<p>Der Baron de Ley präsentiert sich in einer tiefen, intensiv-kirschroten  Farbe. Bereits bevor das Glas geschwenkt wird, lässt Anklänge von geröstetem  Kaffee, von Zimt und Nelke erkennen, die später um Zedern- und Muskatnussaromen  ergänzt werden. Ein ausdrucksvoller Roter, samtig, mit einem langen Finale.</p>', '', 19.5, 12.5, 0, '', 0, 'S4064.jpg', 0, 0, 0, 0, 1, 1, '2012-10-04 11:47:00', 0),
(115, 'TEMPRANILLO D.O. MONASTERIO DE TENTUDIA', 'S4065', '', 'TEMPRANILLO-D-O-MONASTERIO-DE-TENTUDIA', 'TEMPRANILLO D.O. MONASTERIO DE TENTUDIA', '<p>Frisch, leicht, im Gaumen sehr elegant, reichlich vornehme Tannine.</p>', '', 7.5, 5.5, 0, '', 0, 'S4065.jpg', 0, 0, 0, 0, 1, 1, '2012-10-04 11:50:40', 0),
(116, 'PRIORAT D.O. SOLMARENO', 'S4066', '', 'PRIORAT-D-O-SOLMARE-O', 'PRIORAT D.O. SOLMARE?O', '<p>Kräftig und langanhaltend im Geschmack.</p>', '', 7.5, 5.5, 0, '', 0, 'S4066.jpg', 0, 0, 0, 0, 1, 1, '2012-10-04 11:52:41', 0),
(117, 'TEMPRANILLO D.O. DON DANIEL RESERVA', 'S4067', '', 'TEMPRANILLO-D-O-DON-DANIEL-RESERVA', 'TEMPRANILLO D.O. DON DANIEL RESERVA', '<p>Aus selektierten Trauben erzeugter Rotwein mit kraftvollem Charakter und  dominanter Frucht. Durch die ausgeprägten, reifen Tannine hat er Aromen von  Vanille.</p>', '', 11.9, 5.5, 0, '', 0, 'S4067.jpg', 0, 0, 0, 0, 1, 1, '2012-10-04 12:08:46', 0),
(118, 'RIOJA D.O.Ca. LACRIMUS', 'S4068', '', 'RIOJA-D-O-Ca-LACRIMUS', 'RIOJA D.O.Ca. LACRIMUS', '<p>Intensives Kirschrot mit purpurfarbenen Reflexen. Aromen der roter Früchte  und dunkler Waldbeeren mit einem delikaten Anklang von Röstnoten und Karamell.  Am Gaumen einer samtig- weichen Struktur. Die vielschichtige Aromen wilder  Früchte werden durch kühlende Nuancen von Lakritze abgerundet.</p>', '', 12.5, 7.5, 0, '', 0, 'S4068.jpg', 0, 0, 0, 0, 1, 1, '2012-10-04 12:10:15', 0),
(119, 'RIOJA D.O.Ca. TEMPRANILLO RESERVA VINESTRAL', 'S4069', '', 'RIOJA-D-O-Ca-TEMPRANILLO-RESERVA-VINESTRAL', 'RIOJA D.O.Ca. TEMPRANILLO RESERVA VINESTRAL', '<p>Ein klassischer Rioja voll von frischen Beeren von einem schönen glatten  Textur aus guten Gebrauch von Eiche ergänzt.</p>', '', 14.9, 10.5, 0, '', 0, 'S4069.jpg', 0, 0, 0, 0, 1, 1, '2012-10-04 12:12:26', 0),
(120, 'RIOJA D.O.Ca. TEMPRANILLO RESERVA FINCA ROJA', 'S4070', '', 'RIOJA-D-O-Ca-TEMPRANILLO-RESERVA-FINCA-ROJA', 'RIOJA D.O.Ca. TEMPRANILLO RESERVA FINCA ROJA', '<p>Sehr rund und schmackhaft. Aromen von reifen Früchten und charakterisiert  sich geschmacklich durch reife Gerbstoffe und einer süßlichen Würze.</p>', '', 0, 10.5, 0, '', 0, 'S4070.jpg', 0, 0, 0, 0, 1, 1, '2012-10-04 12:14:31', 0),
(121, 'RIOJA D.O.Ca. TEMPRANILLO FINCA ROJA', 'S4071', '', 'RIOJA-D-O-Ca-TEMPRANILLO-FINCA-ROJA', 'RIOJA D.O.Ca. TEMPRANILLO FINCA ROJA', '<p>Sehr rund und schmackhaft. Aromen von reifen Früchten und charakterisiert  sich geschmacklich durch reife Gerbstoffe und einer süßlichen Würze.</p>', '', 12.5, 7.5, 0, '', 0, 'S4071.jpg', 0, 0, 0, 0, 1, 1, '2012-10-04 12:22:14', 0),
(122, 'TEMPRANILLO JUMILLA D.O. VINA PINILLA', 'S4072', '', 'TEMPRANILLO-JUMILLA-D-O-VINA-PINILLA', 'TEMPRANILLO JUMILLA D.O. VINA PINILLA', '<p>Leichter Körper, rustikale Tannine, aber eleganten und unverwechselbar,  erfrischendes Gefühl am Gaumen. Abgegrenzt von roten Früchten, aber der  Nachgeschmack kommt eine leichte Bitterkeit.</p>', '', 0, 5.5, 0, '', 0, 'S4072.jpg', 0, 0, 0, 0, 1, 1, '2012-10-04 12:27:17', 0),
(123, 'TEMPRANILLO RIOJA D.O.Ca. CRIANZA MARQUÉS DE CÀCERES', 'S4073', '', 'TEMPRANILLO-RIOJA-D-O-Ca-CRIANZA-MARQUES-DE-C-CERES', 'TEMPRANILLO RIOJA D.O.Ca. CRIANZA MARQUÉS DE CÀCERES', '<p>Ein vollmundiger Rotwein mit samtiger Struktur, tiefem, vielschichtigem  Fruchtaroma und dezentem Vanille-Eicheholzton, voller Eleganz und Schmelz.  Marques de Caceres tinto wird in defizitären Jahrgängen nicht erzeugt. Diese  Politik wird durch die bedeutenden Bestände der Bodegas an erstklassigen  Rotweinen ermöglicht, die konstante Qualität garantieren.</p>', '', 15.8, 12.5, 0, '', 0, 'S4073.jpg', 0, 0, 0, 0, 1, 1, '2012-10-04 12:29:28', 0),
(124, 'JASPI MARAGDA D.O. MONTSANT', 'S4074', '', 'JASPI-MARAGDA-D-O-MONTSANT', 'JASPI MARAGDA D.O. MONTSANT', '<p>In der Nase verführerisches Bouquet nach asiatischen Gewürzen, Weihrauch,  scharzen Kirschen und Heidelbeeren. Erzeugt im Gaumen zuerst eine angenehme  Empfindung nach reifen roten Beeren und entfaltet dann würzige und balsamische  Noten mit weichen und abgerundeten Tanninen.</p>', '', 19.8, 15.5, 0, '', 0, 'S4074.jpg', 0, 0, 0, 0, 1, 1, '2012-10-04 12:31:20', 0),
(125, 'VERDEJO Y SAUVIGNON BLANC AURUM D.O. YUNTERO', 'S4075', '', 'VERDEJO-Y-SAUVIGNON-BLANC-AURUM-D-O-YUNTERO', 'VERDEJO Y SAUVIGNON BLANC AURUM D.O. YUNTERO', '<p>Farbe: Strohgelb mit Goldreflexen, sehr glänzend. In der Nase präsentiert er  eine schöne, aromatische Intensität mit reifer fruchtig-exotischer und  tropischer Note sowie einem Anisgeschmack im Hintergrund. Im Mund ist er  angenehm, am Gaumen leicht und frisch, sehr fruchtig. Durch seine kräftige  Struktur und Öligkeit hält er im Mund lange nach. Eine tropische Note ist  wahrnehmbar. Er hebt sich ab von den Eigenschaften der sortenreinen Weine der  Rebsorten Verdejo und Sauvignon Blanc.</p>', '', 7.5, 5.5, 0, '', 0, 'S4075.jpg', 0, 0, 0, 0, 1, 1, '2012-10-04 12:32:45', 0),
(126, 'MONTSANT HEREDAD CENTUM D.O.', 'S4076', '', 'MONTSANT-HEREDAD-CENTUM-D-O', 'MONTSANT HEREDAD CENTUM D.O.', '<p>Ein Bukett, welches auf eine lange Lagerzeit in Eichenfässern hindeutet.</p>', '', 15.5, 10.5, 0, '', 0, 'S4076.jpg', 0, 0, 0, 0, 1, 1, '2012-10-04 12:35:00', 0),
(127, 'GARNACHA ROSADO NAVARRA D.O. CASTILUZAR', 'S4077', '', 'GARNACHA-ROSADO-NAVARRA-D-O-CASTILUZAR', 'GARNACHA ROSADO NAVARRA D.O. CASTILUZAR', '<p>Mittlere Intensität, frisch, frisches Obst. Am Gaumen schöne Säure, fruchtig,  geschmackvoll.</p>', '', 0, 5.5, 0, '', 0, 'S4077.jpg', 0, 0, 0, 0, 1, 1, '2012-10-04 13:25:52', 0),
(128, 'RIBERA DEL DUERO D.O. ARROCAL', 'S4078', '', 'RIBERA-DEL-DUERO-D-O-ARROCAL', 'RIBERA DEL DUERO D.O. ARROCAL', '<p>In seinem Bouquet erkennt man den Duft von reifen Himbeeren,  Amarena-Kirschen, Vanille, leichten Röstaromen, sowie eine leichten  Zedernholznote. Am Gaumen ist er konzentriert und kräftig, gepaart mit schönen  runden Tanninen, bestens verbauter Weinsäure und feinen Nuancen vom Fass.</p>', '', 18.5, 12.5, 0, '', 0, 'S4078.jpg', 0, 0, 0, 0, 1, 1, '2012-10-04 13:27:19', 0),
(129, 'RIBERA DEL DUERO D.O. ACAPPELA CRIANZA', 'S4079', '', 'RIBERA-DEL-DUERO-D-O-ACAPPELA-CRIANZA', 'RIBERA DEL DUERO D.O. ACAPPELA CRIANZA', '<p>Trocken, dicht und vielfältig. Reife schwarze Früchte wie Brombeere, Holunder  und schwarze Johannisbeere, dazu Röstaromen, Mokka und etwas  Bitterschokolade.</p>', '', 17.8, 12.5, 0, '', 0, 'S4079.jpg', 0, 0, 0, 0, 1, 1, '2012-10-04 13:28:56', 0),
(130, 'RIBERA DEL DUERO D.O. ACAPPELA ROBLE', 'S4080', '', 'RIBERA-DEL-DUERO-D-O-ACAPPELA-ROBLE', 'RIBERA DEL DUERO D.O. ACAPPELA ROBLE', '<p>Nase nach Waldbeeren und etwas Vanille, voller Körper mit einer kräftigen  Fruchtfülle. Weiches, gut eingebundenes Tannin, langes Finale.</p>', '', 15.9, 10.5, 0, '', 0, 'S4080.jpg', 0, 0, 0, 0, 1, 1, '2012-10-04 13:30:15', 0),
(131, 'MIROS RIBERA DEL DUERO RESERVA D.O.', 'S4081', '', 'MIROS-RIBERA-DEL-DUERO-RESERVA-D-O', 'MIROS RIBERA DEL DUERO RESERVA D.O.', '<p>In der Nase bilden feine balsamische Düfte, reife Fruchtaromen und würzige  Röstaromen ein bezauberndes Bukett. Am Gaumen zeigt er sich körperreich mit  vielfältigen Aromen und mit runden Tanninen, die ihm seine Milde und Länge  verleihen. Ein grosser Wein, der Frucht und Eichenholzaromen wunderbar  vereint.</p>', '', 35.1, 25.5, 0, '', 0, 'S4081.jpg', 0, 0, 0, 0, 1, 1, '2012-10-04 13:32:04', 0),
(132, 'TEMPRANILLO CATALUNYA D.O. MIRET', 'S4082', '', 'TEMPRANILLO-CATALUNYA-D-O-MIRET', 'TEMPRANILLO CATALUNYA D.O. MIRET', '<p>Begeisternd, fruchtiges Bouquet mit leichter Vanille-Note.</p>', '', 9.9, 5.5, 0, '', 0, 'S4082.jpg', 0, 0, 0, 0, 1, 1, '2012-10-04 13:37:45', 0),
(133, 'RIOJA D.O.Ca. RESERVA FAUSTINO RIVERO ULECIA TINTO', 'S4083', '', 'RIOJA-D-O-Ca-RESERVA-FAUSTINO-RIVERO-ULECIA-TINTO', 'RIOJA D.O.Ca. RESERVA FAUSTINO RIVERO ULECIA TINTO', '<p>Trocken, körperreich, komplex, seidig und ausgewogen, langanhaltender  Geschmack.</p>', '', 0, 5.5, 0, '', 0, 'S4083.jpg', 0, 0, 0, 0, 1, 1, '2012-10-04 13:39:15', 0),
(134, 'RIBERA DEL DUERO D.O. ENTRECASTILLOS', 'S4084', '', 'RIBERA-DEL-DUERO-D-O-ENTRECASTILLOS', 'RIBERA DEL DUERO D.O. ENTRECASTILLOS', '<p>Aromen nach Waldfrüchten, kräftig, körperreich, elegant und rund</p>', '', 18.9, 10.5, 0, '', 0, 'S4084.jpg', 0, 0, 0, 0, 1, 1, '2012-10-04 13:40:34', 0),
(135, 'RIOJA D.O.Ca. RESERVA FAUSTINO RIVERO ULECIA CRIANZA', 'S4085', '', 'RIOJA-D-O-Ca-RESERVA-FAUSTINO-RIVERO-ULECIA-CRIANZA', 'RIOJA D.O.Ca. RESERVA FAUSTINO RIVERO ULECIA CRIANZA', '<p>Herrlichen rubinroten, hell und sauber. Leichte Fassaromen mit fruchtigem  Charakter. Der Geschmack ist weich, aber robust, mit guter Struktur und einem  langen Abgang.</p>', '', 0, 7.5, 0, '', 0, 'S4085.jpg', 0, 0, 0, 0, 1, 1, '2012-10-04 13:41:59', 0),
(136, 'SOLAR DE SAEL BIERZO D.O.', 'S4086', '', 'SOLAR-DE-SAEL-BIERZO-D-O', 'SOLAR DE SAEL BIERZO D.O.', '<p>Holzwürziger Duft mit rotbeerigen Noten. Eher einfach im Mund, viel Holz,  vordergründige Art, sandige Tannine und sehr viel Alkohol, knapper Abgang.</p>', '', 0, 10.5, 0, '', 0, 'S4086.jpg', 0, 0, 0, 0, 1, 1, '2012-10-04 13:43:33', 0),
(137, 'TINTA DE TORO D.O. LIBERALIA CUATRO', 'S4087', '', 'TINTA-DE-TORO-D-O-LIBERALIA-CUATRO', 'TINTA DE TORO D.O. LIBERALIA CUATRO', '<p>Dunkles Kirschrot. Kräftiges Aroma, nach süßen Gewürzen, mit cremigen Noten  des Eichenholzes, im Hintergrund nach Konfitüre. Am Gaumen kräftig, fleischig,  mit integrierten und fetten Tanninen, mit sehr reifen sortentypischen  Fruchtnoten, nach Lakritz im Abgang.</p>', '', 18.9, 15.5, 0, '', 0, 'S4087.jpg', 0, 0, 0, 0, 1, 1, '2012-10-04 13:44:55', 0),
(138, 'TINTA DE TORO D.O. LIBERALIA CERO', 'S4088', '', 'TINTA-DE-TORO-D-O-LIBERALIA-CERO', 'TINTA DE TORO D.O. LIBERALIA CERO', '<p>Sehr intensives Kirschrot. Aroma nach roten Früchten, mit cremigen Nuancen  und etwas nach reduzierten Früchten, Schokolade, mineralisch. Am Gaumen  fleischig, mit weichen Tanninen, etwas warm und fett, im Hintergrund nach  Kirschkonfitüre und cremigen Eichenholz.</p>', '', 0, 15.5, 0, '', 0, 'S4088.jpg', 0, 0, 0, 0, 1, 1, '2012-10-04 13:46:30', 0),
(139, 'TINTA DE TORO PRIMA "MAGNUM"', 'S4089', '', 'TINTA-DE-TORO-PRIMA-MAGNUM', 'TINTA DE TORO PRIMA "MAGNUM"', '<p>Purpurrote Farbe. Ein fruchtiges, fein opulentes, süsses Bouquet. Kirsche,  Pflaumen, Dörrfrüchte, Vanille Mocca. Am Gaumen viel Frucht leichte, feine  Tannine, kräftige, süssliche Aromatik, mit einem langen, vollen, fruchtigen und  erfrischenden Finale.</p>', '', 39, 30.5, 0, '', 0, 'S4089.jpg', 0, 0, 0, 0, 1, 1, '2012-10-04 13:47:59', 0),
(140, 'TINTA DE TORO D.O. LIBERALIA CINCO', 'S4090', '', 'TINTA-DE-TORO-D-O-LIBERALIA-CINCO', 'TINTA DE TORO D.O. LIBERALIA CINCO', '<p>Geschmackvoll, frisch und ausladend im Mund; harmonisch eingebundenes  kraftvolles Tannin, Lakritzenoten und Ausdauer sind einige der zahlreichen  Nuancen.</p>', '', 49, 32.5, 0, '', 0, 'S4090.jpg', 0, 0, 0, 0, 1, 1, '2012-10-04 13:49:25', 0),
(141, 'TEMPRANILLO UCLÉS D.O. MESTA FONTANA', 'S4091', '', 'TEMPRANILLO-UCLES-D-O-MESTA-FONTANA', 'TEMPRANILLO UCLÉS D.O. MESTA FONTANA', '<p>Kirschrot mit violettem Rand. Aroma ausdrucksvoll, frisches Obst, rote  Früchte, gute Säure, reife Tannine.</p>', '', 7.9, 5.5, 0, '', 0, 'S4091.jpg', 0, 0, 0, 0, 1, 1, '2012-10-04 13:50:59', 0),
(142, 'TEMPRANILLO UCLÉS D.O. ESENCIA DE FONTANA', 'S4092', '', 'TEMPRANILLO-UCLES-D-O-ESENCIA-DE-FONTANA', 'TEMPRANILLO UCLÉS D.O. ESENCIA DE FONTANA', '<p>Ein Korb voller roter, reif-würziger Beeren mit leichter Schärfe. Elegante  Konzentration und perfekt-eingebundenes Tannin ergötzt den Gaumen.</p>', '', 22.6, 12.5, 0, '', 0, 'S4092.jpg', 0, 0, 0, 0, 1, 1, '2012-10-04 13:52:29', 0),
(143, 'TEMPRANILLO UCLÉS D.O. SELECTION MESTA', 'S4093', '', 'TEMPRANILLO-UCLES-D-O-SELECTION-MESTA', 'TEMPRANILLO UCLÉS D.O. SELECTION MESTA', '<p>Kirschrot mit violettem Rand. Aroma ausdrucksvoll, frisches Obst, rote  Früchte, gute Säure, reife Tannine.</p>', '', 14.95, 10.5, 0, '', 0, 'S4093.jpg', 0, 0, 0, 0, 1, 1, '2012-10-04 13:54:36', 0);
