(150, 'CAVA CRISTALINO BRUT', 'S4100', '', 'CAVA-CRISTALINO-BRUT', 'CAVA CRISTALINO BRUT', '<p>Glänzende, strohgelbe Farbe mit leichten grünen Schattierungen. Angenehme  Perlage mit frischen, fruchtigen Aromen. Weiche und frische Struktur. Komplexes  und gut strukturiertes Bouquet. Leichte Säure mit angenehm langem Abgang.</p>', '', 0, 7.5, 0, '', 0, 'S4100.jpg', 0, 0, 0, 0, 1, 1, '2012-10-04 14:07:25', 0),
(151, 'CAVA CRISTALINO SEMI SECO', 'S4101', '', 'CAVA-CRISTALINO-SEMI-SECO', 'CAVA CRISTALINO SEMI SECO', '<p>Glänzende, strohgelbe Farbe mit leichten grünen Schattierungen. Angenehme  Perlage mit frischen, fruchtigen Aromen. Weiche und frische Struktur. Komplexes  und gut strukturiertes Bouquet.</p>', '', 0, 7.5, 0, '', 0, 'S4101.jpg', 0, 0, 0, 0, 1, 1, '2012-10-04 14:10:01', 0),
