(261, 'ROSSO SALENTO IGT AMORNERO', 'IT1115', '', 'ROSSO-SALENTO-IGT-AMORNERO', 'ROSSO SALENTO IGT AMORNERO', '<p>Rubinrot, reich, komplex und fruchtig, ausgewogen, weich und rund  Fruchtaromen, weichen Tanninen und einem angenehmen Nachgeschmack.</p>', '', 13.8, 7.5, 0, '', 0, 'IT1115.jpg', 0, 0, 0, 0, 1, 1, '2012-10-02 09:33:55', 0),
(262, 'ROSSO SALENTO IGT BELCONTE', 'IT1116', '', 'ROSSO-SALENTO-IGT-BELCONTE', 'ROSSO SALENTO IGT BELCONTE', '<p>Tief intensiv roten Farbton. </p>\r\n<p>Reiches Bouquet mit feinen Anklängen von reifen  Früchten. Guter Körper mit sanften Tanninen und guter Persistenz.</p>', '', 0, 7.5, 0, '', 0, 'IT1116.jpg', 0, 0, 0, 0, 1, 1, '2012-10-02 09:35:19', 0),
(263, 'ROSSO SALENTO IGT DON MATTEO', 'IT1117', '', 'ROSSO-SALENTO-IGT-DON-MATTEO', 'ROSSO SALENTO IGT DON MATTEO', '<p>Rubinrot, reich, komplex und fruchtig, ausgewogen, weich und rund  Fruchtaromen, weichen Tanninen und einem angenehmen Nachgeschmack.</p>', '', 11.4, 7.5, 0, '', 0, 'IT1117.jpg', 0, 0, 0, 0, 1, 1, '2012-10-02 09:36:37', 0),
